import requests 
from bs4 import BeautifulSoup

def scrape_faction_facts(url):
    # Send a GET request to the URL
    try:
        response = requests.get(url)
        # Check if the request was successful (status code 200)
        if response.status_code == 200:
            # Parse the HTML content of the page
            soup = BeautifulSoup(response.content, "html.parser")
            
            # Find the div element with the specified class
            div_element = soup.find_all("div", class_="text")
            
            extracted_text = ""
            # Extract and print the text if the element is found
            if div_element:
                extracted_text += "Dit is de geschiedenis van de partij volgens de Vlaamse Radio- en Televisieomroeporganisatie: "
                extracted_text += "[Bron: " + url + "]"
                for div in div_element:
                    extracted_text += div.get_text(separator="\n")
                extracted_text += "[Bron: " + url + "]"
                extracted_text = extracted_text.replace('\n', '')
                extracted_text = extracted_text.replace('\xa0', '')
                print(repr(extracted_text))
                return extracted_text
            else:
                print("Div element not found.")
                return "No content found."
        else:
            print("Failed to retrieve the webpage.")
            return "No content found."
    except Exception as e:
        print("An error occurred:", str(e))
        return "No content found."

def start_scraper():
    facts_urls = {
        "Vlaams Belang": "https://www.vrt.be/vrtnws/nl/2024/03/25/vlaams-belang-waar-staat-de-partij-voor/",
        "PVDA": "https://www.vrt.be/vrtnws/nl/2024/03/25/pvda-hier-staat-de-partij-voor/",
        "Open Vld": "https://www.vrt.be/vrtnws/nl/2024/03/25/open-vld-hier-staat-de-partij-voor/",
        "Vooruit": "https://www.vrt.be/vrtnws/nl/2024/03/11/vooruit-waar-staat-de-partij-voor/",
        "N-VA": "https://www.vrt.be/vrtnws/nl/2024/03/22/n-va-waar-staat-de-partij-voor/",
        "Groen": "https://www.vrt.be/vrtnws/nl/2024/03/22/groen-waar-staat-de-partij-voor/",
        "cd&v": "https://www.vrt.be/vrtnws/nl/2024/03/22/cd-v-waar-staat-de-partij-voor/",
    }
    
    facts_content = {}
    
    for faction, url in facts_urls.items():
        print(faction)
        facts_content[faction] = scrape_faction_facts(facts_urls[faction])
    
    print(repr(facts_content))

